					ON et_fields.et_field_id = ef.et_field_id
					WHERE (et_fields.org_id = NEW.org_id) AND (et_fields.table_code = 301) AND (ef.et_field_id is null)
					AND (et_fields.is_active = true);
	RETURN NULL;
END;
